import auth from '../globalstate.js/global.jsx'
import { proxy } from "valtio"
import axios from "axios";
import up from '../image-up.svg'
import {Loading} from './loadingScreen'
import { useState } from 'react';


export function Settings(){
  const [respond,setRespond]=useState("")
  const [nameRes,setNameRespond]=useState("")
  const [passRes,setPassWordRes]=useState("")
  const [ageRes,setAgeRespond]=useState("")
  const[show,setShow]=useState(false)
  const[showAlert,setShowAlert]=useState(false)
  
  
  const snapshot=proxy(auth);

 const sendPhoto=async (d)=>{
     const data={
      email: snapshot.email,
      photo: d.currentTarget.result
     }
     
      try{
        const result=await axios.post("https://backendnodejssocialmedia.onrender.com/uploadProfile",data);
        
        if(result.data==="succssefully updating"){
          setShowAlert(true)
        }
      }
      catch(error)
       {
        console.log(error)
      }
      
      
 }
  const logFile=(e)=>{
     
     document.getElementById("profilePhoto").src=e.currentTarget.result;
 }
  
  const uploadImage=()=>{
    const f=document.getElementById("inFile");
    
    if(f.files.length>=1)
    {
      
      const reader = new FileReader();
      let file=new Blob()
      file=f.files[0];
      reader.onload = sendPhoto;
      reader.readAsDataURL(file)
      
    }
    document.getElementById("inFile").value=""
  }


    const handleOnchange=()=>{
      const f=document.getElementById("inFile");
      if(f.files.length>=1)
      {
        
        const reader = new FileReader();
        let file=new Blob()
        file=f.files[0];
        reader.onload = logFile;
        reader.readAsDataURL(file)
        
      }
    }

    const sendNameUpdate=async(obj)=>{
     
       try{
        const result=await axios.post("https://backendnodejssocialmedia.onrender.com/updateUserName",obj)
        
        if(result.data==="name changed")
        setNameRespond("name changed")
         
        }
       catch(e){
        console.log(e)
       }
       
    }
    const sendPhoneUpdate=async(obj)=>{
      try{
        const result=await axios.post("https://backendnodejssocialmedia.onrender.com/updateUserPhone",obj)
        
        if(result.data==="phone changed")
        setRespond("phone changed")
         
        }
       catch(e){
        console.log(e)
       }
   } 
   const sendAgeUpdate=async (obj)=>{
    try{
      const result=await axios.post("https://backendnodejssocialmedia.onrender.com/updateUserAge",obj)
    
      if(result.data==="age changed")
      setAgeRespond("age changed ") 
      }
     catch(e){
      console.log(e)
     }
   }
    const sendUpdates=async (password,newpass)=>{
      const dataForPassword={
        password:password,
        newpass:newpass,
        current:snapshot.email
      }
       
        try{
          const result=await axios.post("https://backendnodejssocialmedia.onrender.com/updateUserPassword",dataForPassword)
          
          if(result.data==="password changed successfully"){
           setPassWordRes("password changed ")
           document.getElementById("password").classList.remove("error")
           document.getElementById("password").value=""
          }
          else{
           document.getElementById("password").classList.add("error")
           document.getElementById("password").value=""
          }
        }
        catch(e){
          console.log(e)
        }
        setShow(false);
    }

    const sendDescriptionUpdate=async(obj)=>{
      try{
        const result=await axios.post("https://backendnodejssocialmedia.onrender.com/sendDescriptionName",obj)
        
        if(result.data==="description changed")
        setAgeRespond("description changed ") 
        }
       catch(e){
        console.log(e)
       }
    }

    const controllInput=()=>{
      setNameRespond("")
      setPassWordRes('')
      setRespond('')
      setAgeRespond('')
      let name=document.getElementById("name").value;
      /* let email=document.getElementById("email").value; */
      let phone=document.getElementById("phone").value;
      let password=document.getElementById("password").value;
      let newpass=document.getElementById("newpassword").value;
      let age=document.getElementById("age").value;
      let description=document.getElementById("description").value;
      
      if(name!=="")
        {if(!isNaN(name) || name.length<3){
          
          document.getElementById("name").classList.add("error")
          document.getElementById("name").value=""
        }
        else{
          document.getElementById("name").classList.remove("error")
          sendNameUpdate({name: name , current: snapshot.email})
          document.getElementById("name").value=""
         }
      }

      if(phone!=="")
        {if(isNaN(phone) || phone.length<8){
         
          document.getElementById("phone").classList.add("error")
          document.getElementById("phone").value=""
        }
        else{
          document.getElementById("phone").classList.remove("error")
          sendPhoneUpdate({phone: phone , current: snapshot.email})
          document.getElementById("phone").value=""
         }
      }
    
      
      if(password!=="" && newpass!=="")
      {
        setShow(true);
        sendUpdates(password,newpass)
      }
      if(age!==""){
        
          if(isNaN(age) || age.length>2){
         
          document.getElementById("age").classList.add("error")
          document.getElementById("age").value=""
        }
        else{
          document.getElementById("age").classList.remove("error")
          sendAgeUpdate({age: age , current: snapshot.email})
          document.getElementById("age").value=""
         }
      }

      if(description!==""){
        
        
          if(description.length>30){
         
          document.getElementById("description").classList.add("error")
          document.getElementById("description").value=""
        }
        else{
          document.getElementById("description").classList.remove("error")
          sendDescriptionUpdate({description: description , current: snapshot.email})
          document.getElementById("description").value=""
         }
      }
      
    }
  
    const suc=<div className='wr'><div className="wrapperAlert">

    <div className="contentAlert">

      <div className="topHalf">

        <p><svg viewBox="0 0 512 512" width="100" title="check-circle">
          <path d="M504 256c0 136.967-111.033 248-248 248S8 392.967 8 256 119.033 8 256 8s248 111.033 248 248zM227.314 387.314l184-184c6.248-6.248 6.248-16.379 0-22.627l-22.627-22.627c-6.248-6.249-16.379-6.249-22.628 0L216 308.118l-70.059-70.059c-6.248-6.248-16.379-6.248-22.628 0l-22.627 22.627c-6.248 6.248-6.248 16.379 0 22.627l104 104c6.249 6.249 16.379 6.249 22.628.001z" />
          </svg></p>
        <h1>Done</h1>
      </div>

      <div className="bottomHalf">

        <p>You updated your profile picture successfully...</p>

        <button id="alertMO" onClick={()=>setShowAlert(false)}>Moving On</button>

      </div>

    </div>        

</div>
</div>
const del=async ()=>{
  var b;
      b=window.confirm("Are you sure")
  if(b===true)
    {
      try{
      const result=await axios.delete(`https://backendnodejssocialmedia.onrender.com/delete/?email=${snapshot.email}`)
      
     if(result.data==="done")
       window.location.replace("/");
    }
    catch(e){
      console.log(e)
    }
  }
}

const sendDefaultPic=async ()=>{
  const data={
    email: snapshot.email,
    photo: "data:image/jpeg;base64,"
   }
   
    try{
      const result=await axios.post("https://backendnodejssocialmedia.onrender.com/uploadProfile",data);
      
      if(result.data==="succssefully updating"){
        setShowAlert(true)
        document.getElementById("profilePhoto").src=data.photo
      }
    }
    catch(error)
     {
      console.log(error)
    }
}
    return(
      
      <div className="settingPage">
        {showAlert ?suc : ""}
        {show ?<Loading/> : ""}
        <input type="file" id="inFile" alt="hidden-input" onChange={handleOnchange} style={{position:"absolute",opacity:"0"}}></input>
        <div className="settings">
          <h1 style={{color: "black",fontSize:"29px"}}>Account</h1>
          
          <div className="photo-section">
              <img src={snapshot.photo} alt="profile" id="profilePhoto"></img>
              <img src={up} style={{cursor:"pointer" , width:'25px'}} alt='upload' className='up-img' onClick={()=>{document.getElementById('inFile').click()}}></img>
              <button onClick={uploadImage} className="button-anim">submit</button>
              <button style={{color: "grey"}} className="button-anim" onClick={sendDefaultPic}>remove</button> 
              
          </div>
          <hr style={{width: "100%", color: 'lightgrey', marginTop: "10px"}}></hr>
          
          <div className="change-name">
                <div className="current">
                  <p>current Name</p>
                  <input type="text"  placeholder={snapshot.userName} disabled></input>
                </div>
                <div className="change">
                  <p>new Name</p>
                  <input type="text" id="name" placeholder="new Name"></input>
                </div>
          </div>
          <hr style={{width: "100%", color: 'lightgrey', marginTop: "10px"}}></hr>
          <div className="change-name change-email">
              <div className="current" id="email-c">
                  <p>Email</p>
                  <input id="email" disabled type="text" placeholder={snapshot.email} ></input>
                </div>
                <div className="change" id="phone-c">
                  <p>Phone Number </p>
                  <input type="text" id="phone" placeholder="number"></input>
                </div>
          </div>
          <hr style={{width: "100%", color: 'lightgrey', marginTop: "10px"}}></hr>
          <div className="change-name">
              <div className="current" id="password-c">
                  <p>current password</p>
                  <input type="text" id="password" placeholder="Password" ></input>
                </div>
                <div className="change" id="new-c">
                  <p>new password </p>
                  <input type="text" id='newpassword' placeholder="New pass"></input>
                </div>
          </div>
          <hr style={{width: "100%", color: 'lightgrey', marginTop: "10px"}}></hr>
          <div className="change-name">
              <div className="current" id="des">
                  <p>Description</p>
                  <input type="text" id="description" placeholder="Description" ></input>
                </div>
                <div className="current" id="des">
                  <p>Age</p>
                  <input type="number" id="age" placeholder="change your age" ></input>
                </div>
          </div>
          <hr style={{width: "100%", color: 'lightgrey', marginTop: "10px"}}></hr>
          <div className="delete-account">
              <h3 >Delete Account</h3>
              <div style={{marginLeft: "10px", display: "flex",alignItems:"center",gap:"8px"}}>
                <p >Clicking on delete will delete all your informations !</p>
                <button className="button-anim" onClick={del}>Delete</button>
              </div>
          </div>
          <button className="saveChanges button-anim2" onClick={controllInput}>Save Changes</button>
          <div className='respond'><p>{respond}</p> <p>{nameRes} </p><p>{passRes}</p><p>{ageRes}</p></div>
        </div>
      </div>
    )
}